import React from 'react';
import DiceGif from '../../../assets/dice.gif';
import './game.scss';
import { useState } from 'react';
import { useEffect } from 'react';

const Game = (props) => {

    const GAMEROW = 8, GAMECOL = 8;

    //Initial data
    let initialGame = [];
    let diamondCount = 0;
    let diamnodPos = [];
    const [openCards, setOpenCards] = useState([]);
    for (let i = 0; i < GAMEROW; i++) {
        initialGame[i] = [];
        let hasDiamond = false;
        for (let j = 0; j < GAMECOL; j++) {
            if ((Math.random() * 10) > 4.5 && !hasDiamond) {
                initialGame[i][j] = true;
                diamnodPos.push(i + "" + j);
                diamondCount++;
                hasDiamond = true;
            } else {
                initialGame[i][j] = false;
            }
        }
    }
    console.log(diamnodPos)

    //Utills
    const getPlaceholder = (row, col) => {
        if(openCards.indexOf(row+''+col) != -1){

        }else{
            return <div className='board-place'><i className='fa fa-question'></i></div>
        }
        return row * 10 + (row % 2 === 0 ? col + 1 : GAMECOL - col);
    }

    const openCard = (rowIndex, colIndex) => {
        let newArr = [...openCards];
        newArr.push(rowIndex+''+colIndex);
        setOpenCards(newArr);
    }

    return (
        <>
            <div className='game-container'>
                <h2 className='heading'>Diamond Sweeper</h2>
                <div className='board'>
                    {initialGame.map((row, rowIndex) => {
                        return <div className='row' key={rowIndex+''}>
                            {row.map((col, colIndex) => {
                                return <div className={'col'} key={rowIndex+''+colIndex} onClick={() => openCard(rowIndex, colIndex)}>
                                    {getPlaceholder(rowIndex, colIndex)}
                                </div>
                            })}
                        </div>
                    })}
                </div>
            </div>
        </>
    )
}

export default Game;
